package eg1;

public class Shapes {

		public void areaRectangle(int i, int j) {
			// TODO Auto-generated method stub
			float a=i*j;
			System.out.println(a);
		}

		public void areaSquare(int i) {
			// TODO Auto-generated method stub
			float a=i*i;
			System.out.println(a);
		}

		public void areaTrapezium(int i, int j, int k) {
			// TODO Auto-generated method stub
			float a=((i+j)/2)*k;
			System.out.println(a);
		}
		
	}
	